CREATE EXTENSION IF NOT EXISTS pgcrypto;
CREATE TABLE "public"."loop"("uuid" uuid NOT NULL DEFAULT gen_random_uuid(), "network_uuid" uuid NOT NULL, "owner_uuid" uuid NOT NULL, "name" text NOT NULL, "slug" text NOT NULL, "description" text, "is_archived" boolean NOT NULL DEFAULT false, "is_private" boolean NOT NULL DEFAULT false, "created_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("uuid") , FOREIGN KEY ("owner_uuid") REFERENCES "public"."user"("uuid") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("network_uuid") REFERENCES "public"."network"("uuid") ON UPDATE cascade ON DELETE cascade, UNIQUE ("uuid"));
