CREATE TABLE "public"."loop_user"("loop_uuid" uuid NOT NULL, "user_uuid" uuid NOT NULL, "last_typed_at" timestamptz NOT NULL DEFAULT now(), "last_read_at" timestamptz NOT NULL DEFAULT now(), "joined_at" timestamptz NOT NULL DEFAULT now(), "is_blocked" boolean NOT NULL DEFAULT false, PRIMARY KEY ("loop_uuid","user_uuid") , FOREIGN KEY ("loop_uuid") REFERENCES "public"."loop"("uuid") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("user_uuid") REFERENCES "public"."user"("uuid") ON UPDATE cascade ON DELETE cascade, UNIQUE ("loop_uuid", "user_uuid"));
